    "local-nodes": {
      "discovery": {
        "method": "docker",
        "addressable_ip": "true",
        "servers": [
          {
            "name": "localhost",
            "host": "192.168.224.2",
            "port": 4243
          }
    ],
    "container_port": 8000,
        "image_name": "local/testapp"
      },
      "haproxy": {
        "port": 9091,

      }

    },
    "remote-nodes":{
      "discovery": {
        "method": "docker",
        "servers": [
          {
            "name": "cconnell3",
            "host": "192.168.222.153",
        "port": 4243
      }
        ],
      "container_port": 8000,
        "image_name": "local/testapp"
      },
      "haproxy": {
        "port": 9091,

      }

    },
    "proxy-clients": {
      "discovery": {
        "method": "docker",
        "addressable_ip": "true",
        "servers": [
          {
            "name": "localhost",
            "host": "192.168.224.2",
            "port": 4243
      }
        ],
        "container_port": 9090,
        "image_name": "local/proxy-node"
      },
      "haproxy": {
        "port": 9090,

      }

    },

  },
"haproxy": {
    "reload_command": "systemctl reload haproxy.service",
    "config_file_path": "/etc/haproxy/haproxy.cfg",
    "do_writes": true,
    "do_reloads": true,
    "bind_address": "*",
    "global": [
      "daemon",
      "maxconn 4096",
    ],
    "defaults": [
      "maxconn  2000",
      "timeout  connect 5s",
      "timeout  client  1m",
      "timeout  server  1m",
      "mode http",
    ],
  }
}
```

