+                return EnsureVisibility(node, node.Modifiers, (x, l) => x.WithModifiers(l), SyntaxKind.PrivateKeyword);
+            }
+
+            public override SyntaxNode VisitEventDeclaration(EventDeclarationSyntax node)
+            {
+                return EnsureVisibility(node, node.Modifiers, (x, l) => x.WithModifiers(l), SyntaxKind.PrivateKeyword);
