+	 * @param $method
+	 */
+	protected function _dispatch($method) {
+
+		$arguments = func_get_args();
+		unset($arguments[0]);
+		$arguments[] = $this;
+
+		$this->getDispatcher()->dispatch($method, $arguments);
+
+	}
