+int
+http_parser_parse_url(const char *buf, size_t buflen, int is_connect,
+                      struct http_parser_url *u)
+{
+  enum state s;
+  const char *p;
+  enum http_parser_url_fields uf, old_uf;
+
+  u->port = u->field_set = 0;
+  s = s_req_spaces_before_url;
+  uf = old_uf = UF_MAX;
+
+  for (p = buf; p < buf + buflen; p++) {
+    if ((s = parse_url_char(s, *p, is_connect)) == s_dead) {
+      return 1;
+    }
+
+    /* Figure out the next field that we're operating on */
+    switch (s) {
+      case s_req_schema:
+      case s_req_schema_slash:
+      case s_req_schema_slash_slash:
+        uf = UF_SCHEMA;
+        break;
+
+      case s_req_host:
+        uf = UF_HOST;
+        break;
+
+      case s_req_port:
+        uf = UF_PORT;
+        break;
+
+      case s_req_path:
+        uf = UF_PATH;
+        break;
+
+      case s_req_query_string_start:
+      case s_req_query_string:
+        uf = UF_QUERY;
+        break;
+
+      case s_req_fragment_start:
+      case s_req_fragment:
+        uf = UF_FRAGMENT;
+        break;
+
+      default:
+        assert(!"Unexpected state");
+        return 1;
+    }
+
+    /* Nothing's changed; soldier on */
+    if (uf == old_uf) {
+      u->field_data[uf].len++;
+      continue;
+    }
+
+    /* We ignore the first character in some fields; without this, we end up
+     * with the query being "?foo=bar" rather than "foo=bar". Callers probably
+     * don't want this.
+     */
+    switch (uf) {
+    case UF_QUERY:
+    case UF_FRAGMENT:
+    case UF_PORT:
+        u->field_data[uf].off = p - buf + 1;
+        u->field_data[uf].len = 0;
+        break;
+
+    default:
+        u->field_data[uf].off = p - buf;
+        u->field_data[uf].len = 1;
+        break;
+    }
+
+    u->field_set |= (1 << uf);
+    old_uf = uf;
+  }
+
+  if (u->field_set & (1 << UF_PORT)) {
+    /* Don't bother with endp; we've already validated the string */
+    unsigned long v = strtoul(buf + u->field_data[UF_PORT].off, NULL, 10);

