+{
+    struct mbuf *buf;
+    uint8_t *p;
+    uint32_t len = 0;
+    uint32_t keylen = 0;
+
+    for (buf = STAILQ_FIRST(&r->mhdr); buf->pos >= buf->last; buf = STAILQ_FIRST(&r->mhdr)) {
