+    r->key_start = r->key_end - keylen;
+    buf->pos += len - CRLF_LEN;
+
+    len = CRLF_LEN;
+    while (mbuf_length(buf) < len) {        /* eat CRLF */
+        len -= mbuf_length(buf);
